import React from 'react'

function TodoItems({desc, key,toggleTaskState,index}) {
    return (
        <p key={key}className='form-group mb-2 text-sm md:text-lg'>

            <input className='mr-2 ' type="checkbox"onClick={()=>{toggleTaskState(index,key)}}/>
      <label>{desc}</label>  


      </p>
    )
}

export default TodoItems
