import { useState } from 'react';
import './App.css';
import Doneitems from './components/Done_items.jsx';
import Nav from './components/Nav';
import TodoItems from './components/Todo-Items';

function App() {
  const [todoCategories, setTodoCategories] = useState([]);
  return (
    <>
    <Nav/>
    <main className='relative flex '>
      <div className='w-[300px]  px-3 py-3 text-white bg-green-500'>
        <h3> Work</h3>
       <TodoItems desc="Call Jonas"/>

       <TodoItems desc="Create Template for FWS"/>

       <TodoItems desc="Slideshow for meeting 11:00 AM"/>
       <h4> Accomplished tasks</h4>
       <Doneitems desc="Feed the Dogs"/>
       <Doneitems desc=" Flush the toilet"/>
      </div>

    </main>
    <section className='flex align-center justify-center' id="modal">
        <main  id="modal-container" className='w-[300px] bg-white h-[250px]'>
          <div>

         <input type="text"/>
          </div>
          <div>

         <button>+ Add task</button>
          </div>
          <div>

         <button className='bg-blue-500 text-white px-4 py-2'>Save</button>
          </div>
        </main>
    </section>
    </>
  );
}

export default App;
