            categories["taskDetails"].map(function(Tasks, id) {if (Tasks["status"]===true){

                   return (<TodoItems toggleTaskState={toggleTaskState} key={id} keyid={id} index={index} desc={Tasks["detail"]}/>)
                }

          })
          }



