            categories["taskDetails"].map(function(Tasks, id) {if (Tasks["status"]===false){

                   return (<Doneitems key={id} toggleTaskState={toggleTaskState} keyid={id} index={index}  desc={Tasks["detail"]}/>)
                }

          })
          }


          </div>
            ))
    } 
    </>
    )
}

