import { useState } from 'react';
import './App.css';
import Doneitems from './components/Done_items.jsx';
import Nav from './components/Nav';
import TodoItems from './components/Todo-Items';

function App() {
  const [todoCategories, setTodoCategories] = useState([]);
  const displayModal = ()=>{
    const element = document.getElementById("modal");
    element.classList.remove("hidden");
  }
  const closeModal = ()=>{
    const element = document.getElementById("modal");
    element.classList.add("hidden");
  }
  return (
    <>
    <Nav/>
    <button onClick={ displayModal}id="custom-btn">
    <i className="text-2xl text-blue-500 fas fa-plus"></i>
      </button>
    <main className='font-poppins px-3 py-3 relative flex '>
      <div className='w-[300px] rounded-br-3xl  px-3 py-3 text-white bg-green-500'>
        <h3 className="font-bold"> Work</h3>
       <TodoItems desc="Call Jonas"/>

       <TodoItems desc="Create Template for FWS"/>

       <TodoItems desc="Slideshow for meeting 11:00 AM"/>
       <h4> Accomplished tasks</h4>
       <Doneitems desc="Feed the Dogs"/>
       <Doneitems desc=" Flush the toilet"/>
      </div>

    </main>
    <section className=' hidden flex items-center justify-center' id="modal">
        <main  id="modal-container" className='relative px-4 py-5 w-[300px] bg-white '>
        <button className='bg-white' onClick={ closeModal} id="custom-btn1">
    <i className="text-2xl text-blue-500 fas fa-times"></i>
      </button>

          <div>

         <input placeholder='Title' className=' font-bold w-[100%] outline-none border-b-2 border-gray-200' type="text"/>
          </div>
          <br/>
          <div>

         <button>+ Add task</button>
          </div>
          <div>
          <br/>
         <button className='bg-blue-500 text-white px-5 py-1'>Save</button>
          </div>
        </main>
    </section>
    </>
  );
}

export default App;
